import React, { useEffect, useState } from "react";
import axios from "axios";

// Replace with your actual base URL and auth token retrieval method
const api = axios.create({
  baseURL: "http://localhost:8000/api",
  headers: {
    Authorization: `Bearer ${localStorage.getItem("authToken") || ""}`, // Adjust if using localStorage or another method
  },
});

export default function Products() {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({
    name: "",
    slug: "",
    discount_price: "",
    stock_keeping_unit: "",
    quantity: "",
    price: "",
    description: "",
    product_images: [], // array of File objects
    status: "",
  });
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await api.get("/products");
      setProducts(
        res.data.map((p) => ({
          ...p,
          images: Array.isArray(p.images) ? p.images : [],
        }))
      );
    } catch (err) {
      console.error("Fetch error:", err.response || err);
    }
  };

  const handleChange = (e) => {
    if (e.target.type === "file") {
      setForm({ ...form, product_images: Array.from(e.target.files) });
    } else {
      setForm({ ...form, [e.target.name]: e.target.value });
    }
  };

  const addProduct = async (e) => {
    e.preventDefault();

    if (form.product_images.length === 0) {
      alert("Please select at least one image");
      return;
    }

    setUploading(true);

    try {
      const formData = new FormData();

      formData.append("name", form.name);
      formData.append("slug", form.slug);
      formData.append("price", form.price);
      formData.append("description", form.description);
      if (form.discount_price) formData.append("discount_price", form.discount_price);
      formData.append("stock_keeping_unit", form.stock_keeping_unit);
      formData.append("quantity", form.quantity);
      formData.append("status", form.status);

      form.product_images.forEach((file) => {
        formData.append("product_images[]", file);
      });

      const res = await api.post("/products", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      setProducts((prev) => [...prev, res.data]);

      // Reset form
      setForm({
        name: "",
        slug: "",
        discount_price: "",
        stock_keeping_unit: "",
        quantity: "",
        price: "",
        description: "",
        product_images: [],
        status: "",
      });

      document.getElementById("productImageInput").value = "";
    } catch (err) {
      console.error("Failed to add product:", err.response || err);
      alert("Error adding product. Check console.");
    } finally {
      setUploading(false);
    }
  };

  const deleteProduct = async (id) => {
    if (!window.confirm("Are you sure you want to delete this product?")) return;

    try {
      await api.delete(`/products/${id}`);
      setProducts((prev) => prev.filter((p) => p.id !== id));
    } catch (err) {
      console.error("Delete error:", err.response || err);
      alert("Error deleting product. Check console.");
    }
  };

  return (
    <div className="container mt-4">
      <h2>Manage Products</h2>

      <form onSubmit={addProduct} className="mb-4">
        <input
          name="name"
          value={form.name}
          onChange={handleChange}
          placeholder="Product Name"
          className="form-control mb-2"
          required
        />
        <input
          name="slug"
          value={form.slug}
          onChange={handleChange}
          placeholder="Slug"
          className="form-control mb-2"
          required
        />
        <input
          name="discount_price"
          type="number"
          value={form.discount_price}
          onChange={handleChange}
          placeholder="Discount Price"
          className="form-control mb-2"
        />
        <input
          name="stock_keeping_unit"
          value={form.stock_keeping_unit}
          onChange={handleChange}
          placeholder="SKU"
          className="form-control mb-2"
          required
        />
        <input
          name="quantity"
          type="number"
          value={form.quantity}
          onChange={handleChange}
          placeholder="Quantity"
          className="form-control mb-2"
          required
        />
        <input
          name="price"
          type="number"
          value={form.price}
          onChange={handleChange}
          placeholder="Price"
          className="form-control mb-2"
          required
        />
        <textarea
          name="description"
          value={form.description}
          onChange={handleChange}
          placeholder="Description"
          className="form-control mb-2"
          required
        />
        <select
          name="status"
          value={form.status}
          onChange={handleChange}
          className="form-control mb-2"
          required
        >
          <option value="">Select Status</option>
          <option value="active">Active</option>
          <option value="inactive">Inactive</option>
        </select>
        <input
          type="file"
          name="product_images"
          id="productImageInput"
          onChange={handleChange}
          className="form-control mb-2"
          multiple
          required
          disabled={uploading}
        />
        <button type="submit" className="btn btn-success" disabled={uploading}>
          {uploading ? "Uploading..." : "Add Product"}
        </button>
      </form>

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Price</th>
            <th>Description</th>
            <th>Discount Price</th>
            <th>SKU</th>
            <th>Quantity</th>
            <th>Slug</th>
            <th>Images</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {products.map((p) => (
            <tr key={p.id}>
              <td>{p.id}</td>
              <td>{p.name}</td>
              <td>{p.price}</td>
              <td>{p.description}</td>
              <td>{p.discount_price}</td>
              <td>{p.stock_keeping_unit}</td>
              <td>{p.quantity}</td>
              <td>{p.slug}</td>
              <td>
                {p.images && p.images.length > 0 ? (
                  p.images.map((img, i) => (
                    <img
                      key={i}
                      src={`http://localhost:8000/${img}`}
                      alt={`${p.name}-${i}`}
                      style={{
                        width: "60px",
                        height: "60px",
                        objectFit: "cover",
                        borderRadius: "4px",
                        marginRight: "5px",
                      }}
                    />
                  ))
                ) : (
                  <span>No Images</span>
                )}
              </td>
              <td>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => deleteProduct(p.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
