import React, { useEffect, useState } from "react";
import api from "../../api/axios"; // your axios instance

export default function Products() {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({
    name: "",
    slug: "",
    discount_price: "",
    stock_keeping_unit: "",
    quantity: "",
    price: "",
    description: "",
    product_images: [],  // File[] before upload
    status: "",
  });
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    api.get("/products")
      .then(res => {
        // Ensure images is parsed JSON array or empty array
        const data = res.data.map(p => ({
          ...p,
          images: p.images ? JSON.parse(p.images) : [],
        }));
        setProducts(data);
      })
      .catch(err => console.error("Fetch error:", err.response || err));
  }, []);

  const handleChange = (e) => {
    if (e.target.type === "file") {
      setForm({ ...form, product_images: Array.from(e.target.files) });
    } else {
      setForm({ ...form, [e.target.name]: e.target.value });
    }
  };

  // Upload images, return array of image paths (strings)
  const uploadImages = async (files) => {
    const formData = new FormData();
    files.forEach(file => formData.append("images[]", file));

    try {
      const res = await api.post("/upload-images", formData, {
        headers: { "Content-Type": "multipart/form-data" }
      });
      return res.data.imagePaths; // Array of strings like ["products/img1.jpg", ...]
    } catch (error) {
      console.error("Image upload failed:", error);
      throw error;
    }
  };

  const addProduct = async (e) => {
    e.preventDefault();

    if (form.product_images.length === 0) {
      alert("Please select at least one image");
      return;
    }

    setUploading(true);

    try {
      // Upload images first
      const imagePaths = await uploadImages(form.product_images);

      // Prepare product payload with images JSON stringified
      const productPayload = {
        name: form.name,
        slug: form.slug,
        price: parseFloat(form.price),
        description: form.description,
        discount_price: form.discount_price ? parseFloat(form.discount_price) : null,
        stock_keeping_unit: form.stock_keeping_unit,
        quantity: parseInt(form.quantity, 10),
        status: form.status,
        images: JSON.stringify(imagePaths), // IMPORTANT: store as JSON string
      };

      // Send product info as JSON
      const res = await api.post("/products", productPayload);

      // Parse images again for frontend display
      const newProduct = { ...res.data, images: imagePaths };

      setProducts(prev => [...prev, newProduct]);

      // Reset form
      setForm({
        name: "",
        slug: "",
        discount_price: "",
        stock_keeping_unit: "",
        quantity: "",
        price: "",
        description: "",
        product_images: [],
        status: "",
      });

      document.getElementById("productImageInput").value = "";

    } catch (err) {
      console.error("Failed to add product:", err);
      alert("Error adding product. Check console.");
    } finally {
      setUploading(false);
    }
  };

  const deleteProduct = (id) => {
    api.delete(`/products/${id}`)
      .then(() => setProducts(products.filter(p => p.id !== id)))
      .catch(err => console.error("Delete error:", err.response || err));
  };

  return (
    <div className="container mt-4">
      <h2>Manage Products</h2>

      <form onSubmit={addProduct} className="mb-4">
        <input
          name="name"
          value={form.name}
          onChange={handleChange}
          placeholder="Product Name"
          className="form-control mb-2"
          required
        />
        <input
          name="slug"
          value={form.slug}
          onChange={handleChange}
          placeholder="Slug"
          className="form-control mb-2"
          required
        />
        <input
          name="discount_price"
          type="number"
          value={form.discount_price}
          onChange={handleChange}
          placeholder="Discount Price"
          className="form-control mb-2"
        />
        <input
          name="stock_keeping_unit"
          value={form.stock_keeping_unit}
          onChange={handleChange}
          placeholder="SKU"
          className="form-control mb-2"
          required
        />
        <input
          name="quantity"
          type="number"
          value={form.quantity}
          onChange={handleChange}
          placeholder="Quantity"
          className="form-control mb-2"
          required
        />
        <input
          name="price"
          type="number"
          value={form.price}
          onChange={handleChange}
          placeholder="Price"
          className="form-control mb-2"
          required
        />
        <textarea
          name="description"
          value={form.description}
          onChange={handleChange}
          placeholder="Description"
          className="form-control mb-2"
          required
        />
        <select
          name="status"
          value={form.status}
          onChange={handleChange}
          className="form-control mb-2"
          required
        >
          <option value="">Select Status</option>
          <option value="active">Active</option>
          <option value="inactive">Inactive</option>
        </select>
        <input
          type="file"
          name="product_images"
          id="productImageInput"
          onChange={handleChange}
          className="form-control mb-2"
          multiple
          required
          disabled={uploading}
        />
        <button
          type="submit"
          className="btn btn-success"
          disabled={uploading}
        >
          {uploading ? "Uploading..." : "Add Product"}
        </button>
      </form>

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>ID</th><th>Name</th><th>Price</th><th>Description</th>
            <th>Discount Price</th><th>SKU</th><th>Quantity</th>
            <th>Slug</th><th>Images</th><th>Action</th>
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p.id}>
              <td>{p.id}</td>
              <td>{p.name}</td>
              <td>{p.price}</td>
              <td>{p.description}</td>
              <td>{p.discount_price}</td>
              <td>{p.stock_keeping_unit}</td>
              <td>{p.quantity}</td>
              <td>{p.slug}</td>
              <td>
                {p.images && p.images.length > 0 ? (
                  p.images.map((img, i) => (
                    <img
                      key={i}
                      src={`http://localhost:8000/${img}`}
                      alt={`${p.name}-${i}`}
                      style={{
                        width: "60px",
                        height: "60px",
                        objectFit: "cover",
                        borderRadius: "4px",
                        marginRight: "5px",
                      }}
                    />
                  ))
                ) : (
                  <span>No Images</span>
                )}
              </td>
              <td>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => deleteProduct(p.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
