import React, { useEffect, useState } from "react";
import api from "../../api/axios";

export default function Products() {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({
    name: "",
    slug: "",
    discount_price: "",
    stock_keeping_unit: "",
    quantity: "",
    price: "",
    description: "",
    status: "",
  });
  const [productImages, setProductImages] = useState([]);
  const [uploading, setUploading] = useState(false);

  // State to manage image preview modal
  const [previewImage, setPreviewImage] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await api.get("/products");
      const data = res.data.map((p) => ({
        ...p,
        images: p.images ? p.images.map((img) => img.image_path) : [],
      }));
      setProducts(data);
    } catch (error) {
      console.error("Fetch products failed:", error);
    }
  };

  const handleChange = (e) => {
    if (e.target.type === "file") {
      setProductImages(Array.from(e.target.files));
    } else {
      setForm((prev) => ({
        ...prev,
        [e.target.name]: e.target.value,
      }));
    }
  };

  const addProduct = async (e) => {
    e.preventDefault();

    if (productImages.length === 0) {
      alert("Please select at least one image");
      return;
    }

    setUploading(true);

    try {
      const formData = new FormData();
      Object.entries(form).forEach(([key, value]) => {
        formData.append(key, value);
      });
      productImages.forEach((file) => {
        formData.append("product_images[]", file);
      });

      const res = await api.post("/products", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      const newProduct = res.data;
      newProduct.images = newProduct.images.map((img) => img.image_path);

      setProducts((prev) => [...prev, newProduct]);

      setForm({
        name: "",
        slug: "",
        discount_price: "",
        stock_keeping_unit: "",
        quantity: "",
        price: "",
        description: "",
        status: "",
      });
      setProductImages([]);
      document.getElementById("productImageInput").value = null;
    } catch (err) {
      console.error("Error:", err.response?.data || err.message);
      if (err.response?.status === 422) {
        alert("Validation errors: " + JSON.stringify(err.response.data.errors));
      } else {
        alert("Something went wrong. Check console.");
      }
    } finally {
      setUploading(false);
    }
  };

  const deleteProduct = async (id) => {
    if (!window.confirm("Are you sure you want to delete this product?")) return;

    try {
      await api.delete(`/products/${id}`);
      setProducts((prev) => prev.filter((p) => p.id !== id));
    } catch (error) {
      console.error("Delete error:", error);
      alert("Failed to delete product.");
    }
  };

  // Close preview modal
  const closePreview = () => setPreviewImage(null);

  return (
    <div className="container mt-4">
      <h2>Manage Products</h2>

      <form onSubmit={addProduct} className="mb-4">
        {/* ...form inputs same as before... */}
        <input
          name="name"
          value={form.name}
          onChange={handleChange}
          placeholder="Product Name"
          className="form-control mb-2"
          required
        />
        {/* ...rest of the inputs */}
        <input
          type="file"
          name="product_images"
          id="productImageInput"
          onChange={handleChange}
          className="form-control mb-2"
          multiple
          required
          disabled={uploading}
          accept="image/*"
        />
        <button type="submit" className="btn btn-success" disabled={uploading}>
          {uploading ? "Uploading..." : "Add Product"}
        </button>
      </form>

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>ID</th><th>Name</th><th>Price</th><th>Description</th>
            <th>Discount Price</th><th>SKU</th><th>Quantity</th><th>Slug</th><th>Images</th><th>Action</th>
          </tr>
        </thead>
        <tbody>
          {products.map((p) => (
            <tr key={p.id}>
              <td>{p.id}</td>
              <td>{p.name}</td>
              <td>{p.price}</td>
              <td>{p.description}</td>
              <td>{p.discount_price}</td>
              <td>{p.stock_keeping_unit}</td>
              <td>{p.quantity}</td>
              <td>{p.slug}</td>
              <td>
                {p.images && p.images.length > 0 ? (
                  p.images.map((img, i) => (
                    <img
                      key={i}
                      src={`http://localhost:8000/${img}`}
                      alt={`${p.name}-${i}`}
                      style={{
                        width: "60px",
                        height: "60px",
                        objectFit: "cover",
                        borderRadius: "4px",
                        marginRight: "5px",
                        cursor: "pointer",
                      }}
                      onClick={() => setPreviewImage(`http://localhost:8000/${img}`)}
                      title="Click to preview"
                    />
                  ))
                ) : (
                  <span>No Images</span>
                )}
              </td>
              <td>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => deleteProduct(p.id)}
                  disabled={uploading}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal for Image Preview */}
      {previewImage && (
        <div
          onClick={closePreview}
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: "rgba(0,0,0,0.7)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 1000,
            cursor: "pointer",
          }}
          title="Click anywhere to close"
        >
          <img
            src={previewImage}
            alt="Preview"
            style={{
              maxHeight: "90vh",
              maxWidth: "90vw",
              borderRadius: "8px",
              boxShadow: "0 0 10px #fff",
            }}
          />
        </div>
      )}
    </div>
  );
}
