import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [form, setForm] = useState({ name: '', email: '' });
  const [message, setMessage] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Dummy login credentials for demo (adjust as needed)
  const loginCredentials = {
    email: 'user@example.com',
    password: 'password123',
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Function to perform login first (CSRF + login)
  const performLogin = () => {
    return axios.get('http://localhost:8000/sanctum/csrf-cookie', { withCredentials: true })
      .then(() => {
        return axios.post('http://localhost:8000/api/contact', loginCredentials, { withCredentials: true });
      })
      .then(() => {
        setIsLoggedIn(true);
      });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const submitForm = () => {
      axios.post('http://localhost:8000/api/contacts', form, {
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        },
        withCredentials: true
      })
        .then(res => {
          setMessage(res.data.message);
          setForm({ name: '', email: '' });
        })
        .catch(err => {
          console.error('Full error:', err);
          if (err.response && err.response.data.errors) {
            setMessage(Object.values(err.response.data.errors).join(' '));
          } else if (err.response && err.response.data.message) {
            setMessage(err.response.data.message);
          } else {
            setMessage('Error submitting form');
          }
        });
    };

    if (!isLoggedIn) {
      // First login, then submit form
      performLogin()
        .then(() => {
          submitForm();
        })
        .catch(() => {
          setMessage('Login failed. Cannot submit form.');
        });
    } else {
      // Already logged in, just submit
      submitForm();
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Contact Form</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label><br />
          <input
            type="text"
            name="name"
            value={form.name}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Email:</label><br />
          <input
            type="email"
            name="email"
            value={form.email}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit" style={{ marginTop: '1rem' }}>Submit</button>
      </form>
    </div>
  );
}

export default App;
