import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie'; // ✅ Add this

function App() {
  const [contactForm, setContactForm] = useState({ name: '', email: '' });
  const [message, setMessage] = useState('');

  // 1. Get CSRF cookie once on component mount
  useEffect(() => {
    axios.get('http://localhost:8000/sanctum/csrf-cookie', { withCredentials: true })
      .then(() => {
        console.log('CSRF cookie set');
      })
      .catch((error) => {
        console.error('Failed to set CSRF cookie:', error);
      });
  }, []);

  // 2. Handle contact form input change
  const handleContactChange = (e) => {
    setContactForm({ ...contactForm, [e.target.name]: e.target.value });
  };

  // 3. Contact form submit handler
  const handleContactSubmit = (e) => {
    e.preventDefault();

    const csrfToken = Cookies.get('XSRF-TOKEN'); // ✅ Get CSRF token from cookie

    axios.post('http://localhost:8000/api/contacts', contactForm, {
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'X-XSRF-TOKEN': csrfToken // ✅ Include CSRF token in headers
      },
      withCredentials: true
    })
      .then(res => {
        setMessage(res.data.message || 'Form submitted successfully!');
        setContactForm({ name: '', email: '' });
      })
      .catch(err => {
        console.error('Full error:', err);
        if (err.response && err.response.data.errors) {
          setMessage(Object.values(err.response.data.errors).join(' '));
        } else if (err.response && err.response.data.message) {
          setMessage(err.response.data.message);
        } else {
          setMessage('Error submitting form');
        }
      });
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Contact Form</h2>
      <form onSubmit={handleContactSubmit}>
        <div>
          <label>Name:</label><br />
          <input
            type="text"
            name="name"
            value={contactForm.name}
            onChange={handleContactChange}
            required
          />
        </div>
        <div>
          <label>Email:</label><br />
          <input
            type="email"
            name="email"
            value={contactForm.email}
            onChange={handleContactChange}
            required
          />
        </div>
        <button type="submit" style={{ marginTop: '1rem' }}>Submit</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default App;
