import React, { useEffect, useState } from "react";
import api from "../../api/axios";

export default function Products() {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({
    name: "",
    slug: "",
    discount_price: "",
    stock_keeping_unit: "",
    quantity: "",
    price: "",
    description: "",
    product_images: [], // multiple files
    status: "",
  });

  useEffect(() => {
    api.get("/products")
      .then(res => setProducts(res.data))
      .catch(err => console.error(err.response || err));
  }, []);

  const handleChange = (e) => {
    if (e.target.type === "file") {
      setForm({ ...form, product_images: Array.from(e.target.files) });
    } else {
      setForm({ ...form, [e.target.name]: e.target.value });
    }
  };

  const addProduct = (e) => {
    e.preventDefault();

    const formData = new FormData();

    // Append normal fields
    Object.entries(form).forEach(([key, value]) => {
      if (key !== "product_images") {
        formData.append(key, value);
      }
    });

    // Append each image file without the []
    form.product_images.forEach((file) => {
      formData.append("product_images", file);
    });

    api.post("/products", formData)
      .then((res) => {
        setProducts([...products, res.data]);
        setForm({
          name: "",
          slug: "",
          discount_price: "",
          stock_keeping_unit: "",
          quantity: "",
          price: "",
          description: "",
          product_images: [],
          status: "",
        });
        document.getElementById("productImageInput").value = ""; // reset file input
      })
      .catch(err => {
        if (err.response && err.response.data && err.response.data.errors) {
          alert(JSON.stringify(err.response.data.errors));
          console.error('Validation errors:', err.response.data.errors);
        } else {
          console.error(err.response || err);
        }
      });
  };

  const deleteProduct = (id) => {
    api.delete(`/products/${id}`)
      .then(() => setProducts(products.filter(p => p.id !== id)))
      .catch(err => console.error(err.response || err));
  };

  return (
    <div className="container mt-4">
      <h2>Manage Products</h2>

      <form onSubmit={addProduct} className="mb-4">
        <input
          name="name"
          value={form.name}
          onChange={handleChange}
          placeholder="Product Name"
          className="form-control mb-2"
          required
        />
        <input
          name="slug"
          value={form.slug}
          onChange={handleChange}
          placeholder="Slug"
          className="form-control mb-2"
          required
        />
        <input
          name="discount_price"
          type="number"
          value={form.discount_price}
          onChange={handleChange}
          placeholder="Discount Price"
          className="form-control mb-2"
        />
        <input
          name="stock_keeping_unit"
          value={form.stock_keeping_unit}
          onChange={handleChange}
          placeholder="SKU"
          className="form-control mb-2"
          required
        />
        <input
          name="quantity"
          type="number"
          value={form.quantity}
          onChange={handleChange}
          placeholder="Quantity"
          className="form-control mb-2"
          required
        />
        <input
          name="price"
          type="number"
          value={form.price}
          onChange={handleChange}
          placeholder="Price"
          className="form-control mb-2"
          required
        />
        <textarea
          name="description"
          value={form.description}
          onChange={handleChange}
          placeholder="Description"
          className="form-control mb-2"
          required
        />
        <select
          name="status"
          value={form.status}
          onChange={handleChange}
          className="form-control mb-2"
          required
        >
          <option value="">Select Status</option>
          <option value="active">Active</option>
          <option value="inactive">Inactive</option>
        </select>
        <input
          type="file"
          name="product_images"
          id="productImageInput"
          onChange={handleChange}
          className="form-control mb-2"
          multiple
          required
        />
        <button type="submit" className="btn btn-success">
          Add Product
        </button>
      </form>

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>ID</th><th>Name</th><th>Price</th><th>Description</th>
            <th>Discount Price</th><th>SKU</th><th>Quantity</th>
            <th>Slug</th><th>Product Images</th><th>Action</th>
          </tr>
        </thead>
        <tbody>
          {products.map((p) => (
            <tr key={p.id}>
              <td>{p.id}</td>
              <td>{p.name}</td>
              <td>{p.price}</td>
              <td>{p.description}</td>
              <td>{p.discount_price}</td>
              <td>{p.stock_keeping_unit}</td>
              <td>{p.quantity}</td>
              <td>{p.slug}</td>
              <td>
                {p.images && p.images.length > 0 ? (
                  p.images.map((img, index) => (
                    <img
                      key={index}
                      src={`http://localhost:8000/${img}`}
                      alt={p.name}
                      style={{
                        width: "60px",
                        height: "60px",
                        objectFit: "cover",
                        borderRadius: "4px",
                        marginRight: "5px",
                      }}
                    />
                  ))
                ) : (
                  <span>No Images</span>
                )}
              </td>
              <td>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => deleteProduct(p.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
